import React, { useState, useEffect } from 'react';
import defaultProfileImage from '../../res/default/profile.jpg'

import './Profile.css';

function Profile() {
    // vous pouvez récupérer l'ID de l'utilisateur à partir du stockage local
    const userId = localStorage.getItem('userId');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [profileImage, setProfileImage] = useState('');
    const [creationDate, setCreationDate] = useState('');
    const [bio, setBio] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const defaultBio = 'Pas de bio ?\nPas de problème... Vous êtes mystérieux !';

    // Récupérer les informations de l'utilisateur
    const url = `http://localhost:3001/user/${userId}`;
    console.log(url);
    useEffect(() => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setUsername(data.pseudo);
                setEmail(data.email);
                setProfileImage(data.profileImage);
                setBio(data.bio);
                setCreationDate(data.registrationDateTime);
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des informations de l\'utilisateur :', error);
                setErrorMessage('Erreur lors de la récupération des informations de l\'utilisateur');
            });
    }, [url]);

    if (errorMessage) {
        return <div>{errorMessage}</div>;
    }

    if (!username) {
        return <div>Chargement...</div>;
    }

    return (
        <div className="profile_container">
            <img src="https://images.unsplash.com/photo-1557683316-973673baf926" alt="Banner" className="profile_banner" />
            <div className="profile_main">
                <img src={profileImage || defaultProfileImage} alt="Profile" className="profile_image" />
                <div className="profile_info">
                    <div className="profile_username">{username}</div>
                    <div className="profile_creation">Membre depuis {Math.floor((new Date() - new Date(creationDate)) / (1000 * 60 * 60 * 24))} jours</div>
                </div>
            </div>
            <div className="profile_bio_container">
                <div className="profile_bio_title">Bio</div>
                <div className="profile_bio_content">{bio}</div>
            </div>
        </div>
    );
}
export default Profile;
