import React from 'react';
import './Home.css';
import { CartProvider } from '../CartContext'; // Vérifiez que le chemin d'importation est correct

import ApiClient from '../ApiClient.js';

import Header from '../components/Header';
import GamePres from '../components/home/GamePres';
import AllGames from '../components/home/AllGames';


function Home() {
  const [games, setGames] = useState([]);
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/')
      .then(response => response.json())
      .then(data => {
        setGames(data);
      })
      .catch(error => console.error('Erreur lors de la récupération des jeux :', error));

    fetch('http://localhost:3001/images/')
      .then(response => response.json())
      .then(data => {
        setImages(data);
      })
      .catch(error => console.error('Erreur lors de la récupération des images :', error));
  }, []);

  return (
    /*
    <CartProvider>
      <div className="App">
        <Header hide={true} />
        <GamePres games={games} />
        <AllGames games={games} />
      </div>
    </CartProvider>  
    */
    <div></div>   
  );
}

export default Home;
