import './GameCard.css'
import shop from '../../../res/icon/shopping-cart.png'

import { useCart } from '../../CartContext';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';


function GameCard(props) {
    var game = props.c_game;
    const { addToCart } = useCart();
    const [image, setImage] = useState(null);

    useEffect(() => {
        if (game) {
            setImage(game.image);
        }
    }, []);
    
    return (
        <div className="game-card">
            <h2 className="game-card-title">{game.title}</h2>
            <img src={image["path"]} alt={game.title} className="game-card-image" />
            <div className="game-card-buy">
                <p className="game-card-price">Prix : {game.price}â‚¬</p>
                <button className="game-card-button" onClick={() => addToCart(game)}>
                    <img src={shop} alt="Ajouter au panier" className='game-card-button-icon' />
                </button>
            </div>
            <Link to={`/detail/${game.id}`} className="game-card-link">En savoir plus</Link>
        </div>
    );
}

export default GameCard;
