import React, { useEffect } from 'react';
import ChromeDinoGame from "react-chrome-dino";
import './Home.css';
import { CartProvider } from '../CartContext'; // Vérifiez que le chemin d'importation est correct

import Header from '../components/Header';
import GamePres from '../components/home/GamePres';
import AllGames from '../components/home/AllGames';


function Home() {
  useEffect(() => {
    const runners = document.querySelectorAll('.runner-container');
    // Si runners est défini et supérieur à 0, alors on en gardera un seul
    if (runners && runners.length > 0) {
      for (let i = 1; i < runners.length; i++) {
        runners[i].remove();
      }
    }
  }
  , []);
  return (
    <CartProvider>
      <div className="App">
        <Header hide={true} />
        <GamePres />
        <AllGames />
        <ChromeDinoGame />
      </div>
    </CartProvider>  
  );
}

export default Home;
