import "./Login.css";


function Login() {
  const [email, setEmail] = useState(''); // Etat qui Stocke l'email de l'utilisateur
  const [password, setPassword] = useState(''); // Etat qui Stocke le mot de passe de l'utilisateur.
  const [isSignup, setIsSignup] = useState(false);
  const [errorMessage, setErrorMessage] = useState(''); // Stocke les messages d'erreur à afficher.
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = isSignup ? 'http://localhost:3001/signup' : 'http://localhost:3001/login';
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });
      if (!response.ok) {
        const errorMessage = await response.text();
        setErrorMessage(errorMessage);
        throw new Error(errorMessage);
      }
      const data = await response.json(); // on récupère les données du json si ok
      const userId = data.userId; // on récupère le userId
      localStorage.setItem('userId', userId); // Stocke ID de l'utilisateur dans le stockage local
      window.location.href = '/profile'; 
    } catch (error) {
      console.error('Erreur lors de la connexion :', error);
      setErrorMessage('Identifiants incorrects. Veuillez réessayer.'); // Affiche message erreur à
      utilisateur
    }
  };

  return (
    <div className="login_container">
      <img src="https://images.unsplash.com/photo-1557683316-973673baf926" alt="login background" className="login_background" />
      <div className="login_main">
        <text className="login_title">Login</text>
        <text className="login_register">You don't have an account? <a href="/register">Register</a></text>
        <form className="login_form">
          <div className="input">
            <label className="login_label">Username</label>
            <input type="text" className="login_input" />
          </div>
          <div className="input">
            <label className="login_label">Password</label>
            <input type="password" className="login_input" />
          </div>
          <div className="remember">
            <input type="checkbox" className="login_checkbox" />
            <label className="login_checkbox_label">Remember me</label>
          </div>
          <button className="login_button">Login</button>
        </form>
        <text className="login_forgot">Forgot your password?<a href="/forgot">Click here</a></text>
      </div>
    </div>
  );
}

export default Login;
