import React, { useState, useEffect } from 'react';
import defaultProfileImage from '../../res/default/profile.jpg'

import './Profile.css';

function Profile() {
    // vous pouvez r√©cup√©rer l'ID de l'utilisateur √† partir du stockage local
    const userId = localStorage.getItem('userId');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [profileImage, setProfileImage] = useState('');
    const [creationDate, setCreationDate] = useState('');
    const [bio, setBio] = useState('');
    const [games, setGames] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    const defaultBio = 'Pas de bio ?\nPas de probl√®me... Vous √™tes myst√©rieux ! üòé';

    // R√©cup√©rer les informations de l'utilisateur
    const url = `http://localhost:3001/user/${userId}`;
    console.log(url);
    useEffect(() => {
        fetch(url)
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setUsername(data.pseudo);
                setEmail(data.email);
                setProfileImage(data.profileImage);
                setBio(data.bio);
                setCreationDate(data.registrationDateTime);
            })
            .catch(error => {
                console.error('Erreur lors de la r√©cup√©ration des informations de l\'utilisateur :', error);
                setErrorMessage('Erreur lors de la r√©cup√©ration des informations de l\'utilisateur');
            });
    }, [url]);

    if (errorMessage) {
        return <div>{errorMessage}</div>;
    }

    if (!username) {
        return <div>Chargement...</div>;
    }

    return (
        <div className="profile_container">
            <img src="https://images.unsplash.com/photo-1557683316-973673baf926" alt="Banner" className="profile_banner" />
            <div className="profile_main">
                <img src={profileImage || defaultProfileImage} alt="Profile" className="profile_image" />
                <div className="profile_info">
                    <div className="profile_username">{username}</div>
                    <div className="profile_creation">Membre depuis {Math.floor((new Date() - new Date(creationDate)) / (1000 * 60 * 60 * 24))} jours</div>
                </div>
            </div>
            <div className="profile_info_main_container">
                <div className="profile_info_container">
                    <div className="profile_info_title">Bio</div>
                    <text className="profile_info_content_text" style={{ whiteSpace: 'pre-line' }}>{bio || defaultBio}</text>
                </div>
            </div>
            <div className="profile_games_info_container profile_info_main_container">
                <div className="profile_info_container profile_achievements_container">
                    <div className="profile_info_title">Achievements</div>
                    <div className="profile_info_content">0</div>
                </div>
                <div className="profile_info_container profile_games_container">
                    <div className="profile_info_title">Games</div>
                    <div className="profile_info_content">
                        {
                            games
                        }
                    </div>
                </div>
            </div>
        </div>
    );
}
export default Profile;
