import GameCard from "./GameCard";


import './AllGames.css';

function AllGames() {
    const [games, setGames] = useState([]);
    const [images, setImages] = useState([]);
    
    console.log("hello");
    useEffect(() => {
        console.log('Fetching games');

        fetch('http://localhost:3001/')
        .then(response => response.json())
        .then(data => {
            console.log(data);
            setGames(data);
        })
        .catch(error => console.error('Erreur lors de la récupération des jeux :', error));

        fetch('http://localhost:3001/images')
        .then(response => response.json())
        .then(data => {
            console.log(data);
            setImages(data);
        })
        .catch(error => console.error('Erreur lors de la récupération des images :', error));
    }, []);

    games.forEach(game => {
        game.image = images.find(image => image.game_id === game.id);
    });

    if (games === undefined || games.length === 0) {
        return (<div>Chargement...</div>);
    }

    return (
        <div className="AllGames">
            <h1 className="AllGames_title">Nous jeux</h1>
            <div className="AllGames_container">
                {games.map((cc_game, index) => (
                    <GameCard c_game={cc_game} key={index} />
                ))}
            </div>
        </div>
    );
}

export default AllGames;
