// URL: http://localhost:3001
// GET / => Récupère tous les jeux
// GET /:id => Récupère un jeu par son ID

const games = [];
const images = [];

// Get all games from the api
fetch('http://localhost:3001/')
    .then(response => response.json())
    .then(data => {
        data.forEach(game => {
            games.push(game);
        });
    })
    .catch(error => console.error('Erreur lors de la récupération des jeux :', error));

// Get all images from the api
fetch('http://localhost:3001/images/')
    .then(response => response.json())
    .then(data => {
        data.forEach(image => {
            images.push(image);
        });
    })
    .catch(error => console.error('Erreur lors de la récupération des images :', error));

const getAllGames = () => {
    return games;
};

const getGame = (id) => {
    return games.find((game) => game.id === parseInt(id));
};



export { getAllGames , getGame };
