import React, { useState, useEffect, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import defaultProfileImage from '../../res/default/profile.jpg';
import back from '../../res/icon/left-arrow.png';
import modify from '../../res/icon/modify.svg';

import './Profile.css';

function Profile() {
    const userId = localStorage.getItem('userId');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [profileImage, setProfileImage] = useState('');
    const [bannerImage, setBannerImage] = useState('https://images.unsplash.com/photo-1557683316-973673baf926');
    const [creationDate, setCreationDate] = useState('');
    const [bio, setBio] = useState('');
    const [games, setGames] = useState([]);
    const [achievements, setAchievements] = useState([]);

    const [errorMessage, setErrorMessage] = useState('');

    const [overBanner, setOverBanner] = useState(false);
    const [overProfile, setOverProfile] = useState(false);

    const defaultBio = 'Pas de bio ?\nPas de problÃ¨me... Vous Ãªtes mystÃ©rieux ! ðŸ˜Ž';

    function isTokenExpired(token) {
        const payload = JSON.parse(atob(token.split('.')[1]));
        return payload.exp * 1000 < Date.now();
    }

    // RÃ©cupÃ©rer les informations de l'utilisateur
    const url = `http://localhost:3001/user/${userId}`;
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token && !isTokenExpired(token)) {
            fetch(url)
                .then(response => response.json())
                .then(data => {
                    console.log(data);
                    setUsername(data.pseudo);
                    setEmail(data.email);
                    setProfileImage(data.profileImage);
                    data.banner && setBannerImage(data.banner);
                    setBio(data.bio);
                    setCreationDate(data.registrationDateTime);
                })
                .catch(error => {
                    console.error('Erreur lors de la rÃ©cupÃ©ration des informations de l\'utilisateur :', error);
                    setErrorMessage('Erreur lors de la rÃ©cupÃ©ration des informations de l\'utilisateur');
                });
        } else {
            localStorage.removeItem('token');
            localStorage.removeItem('userId');
            window.location.href = '/login';
        }
    }, [url]);

    const onDrop = useCallback(acceptedFiles => {
        const file = acceptedFiles[0];
        const reader = new FileReader();
        reader.onload = () => {
            setProfileImage(reader.result);
        };
        reader.readAsDataURL(file);
        uploadProfileImage(file);
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

    const uploadProfileImage = (file) => {
        const url = `http://localhost:3001/user/uploadProfileImage`;
        const formData = new FormData();
        formData.append('profileImage', file);

        fetch(url, {
            method: 'POST',
            headers: {
                userId,
                token: localStorage.getItem('token')
            },
            body: formData
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setProfileImage(data.profileImage);
            })
            .catch(error => {
                console.error('Erreur lors de la mise Ã  jour de l\'image de profil :', error);
                setErrorMessage('Erreur lors de la mise Ã  jour de l\'image de profil');
            });
    };

    if (errorMessage) {
        return <div>{errorMessage}</div>;
    }

    if (!username) {
        return <div>Chargement...</div>;
    }

    return (
        <div className="profile_container">
            <div className="profile_back_home_button" onClick={() => window.location.href = '/'}>
                <img src={back} alt="Back" className="profile_back_home_button_img" />
                <text className="profile_back_home_button_text">Retour Ã  l'accueil</text>
            </div>

            <div alt="Banner" className="profile_banner" style={{ backgroundImage: `url(${bannerImage})` }}
                onMouseEnter={() => setOverBanner(true)} onMouseLeave={() => setOverBanner(false)}>
                {overBanner && <div className="profile_change">
                    <img src={modify} alt="Change banner" className="profile_change_img" />
                </div>}
            </div>

            <div className="profile_main">
                <div {...getRootProps()} alt="Profile" className="profile_image" style={{ backgroundImage: `url(${profileImage || defaultProfileImage})` }}
                    onMouseEnter={() => setOverProfile(true)} onMouseLeave={() => setOverProfile(false)}>
                    <input {...getInputProps()} />
                    {overProfile && <div className="profile_change" style={{ borderRadius: '50%' }}>
                        {isDragActive ? <p>Drop the files here ...</p> : <img src={modify} alt="Change profile" className="profile_change_img" />}
                    </div>}
                </div>
                <div className="profile_info">
                    <div className="profile_username">{username}</div>
                    <div className="profile_creation">Membre depuis {Math.floor((new Date() - new Date(creationDate)) / (1000 * 60 * 60 * 24))} jours</div>
                </div>
            </div>

            <div className="profile_info_container profile_games_container">
                <div className="profile_info_title">Games</div>
                <div className="profile_info_content">
                    {
                        games.length === 0 ?
                            // Si l'utilisateur n'a pas de jeu, affichez un message et un bouton pour ajouter un jeu
                            <div>
                                <text className='profile_info_content_text'>Vous n'avez pas de jeu pour le moment... ðŸ˜¢</text>
                                <button className="profile_add_game_button" onClick={() => window.location.href = '/'}>Ajouter un jeu</button>
                            </div>
                            :
                            // Sinon, affichez les jeux de l'utilisateur
                            <div>
                                {games.map(game => (
                                    <div key={game.id} className="profile_game">
                                        <img src={game.image} alt={game.name} className="profile_game_image" />
                                        <text className="profile_game_name">{game.name}</text>
                                    </div>
                                ))}
                            </div>
                    }
                </div>
            </div>
        </div>
    );
}
export default Profile;
